        }

        if (Comment.length < 3) {
            errorText.innerHTML = ""Mimum Comment Character 2""
            return false;
        }

        if (UserName.length == 0) {
            errorText.innerHTML = ""Please Sign In Or Sign Up""
            return false;
        }
        return true;
    }
</script>

