                   imageUrl: 'https://media.giphy.com/media/xTkcEQACH24SMPxIQg/giphy.gif',
                   imageWidth: 400,
                   imageHeight: 350,
                   imageAlt: 'Loading',
               })

               $.ajax({
                   url: '/Comment/Create',
                   headers: {
                       ""RequestVerificationToken"": $('input[name=""__RequestVerificationToken""]').val()
                   },
                   type: 'POST',
                   data: { UserId: UserName, Comment: Comment, Id: id },
                   success: (response) => {
                       if (response.hasOwnProperty('status') && response.status === 201) {
                           // Comment Success
                           wait.close()
                           Swal.fire(
                               'Comment Accept',
                               '',
                               'success'
                           )
                     ");
